import React from 'react';
import './Card.css';
import posed, {PoseGroup} from 'react-pose';

const Pop = posed.div({
    enter: {
      width:'70%',
      height: '70vh'
    },
    exit: { opacity: 0 }
  });

const Card = ({children}) =>{
    return(
        <Pop className="outerCard">
            {children}
        </Pop>
    )
}

export default Card;
