import React from 'react';
import './NavigationBar.css';
import {Link} from 'react-router-dom';
import posed, { PoseGroup } from "react-pose";

const ListContainer = posed.ul({
    enter: { staggerChildren: 50 },
    exit: { staggerChildren: 20, staggerDirection: -1 }
});

const Pop = {
    hoverable: true,
    init: { scale: 1 },
    hover: { scale: 1.2 }
  }

const Item = posed.li({
    enter: { x: 0, opacity: 1 },
    exit: { x: 50, opacity: 0 }
});

const Container = posed.div({
    enter: { staggerChildren: 50 }
});

const NavigationBar = () =>{
    return(
        <Container className="NavigationContainer">
            <ListContainer>
                <Item><Pop style={{ color: 'inherit',textDecoration: 'inherit' }} to="/">Home</Pop></Item>
                <Item><Pop style={{ color: 'inherit',textDecoration: 'inherit' }} to="/about">About</Pop></Item>
                <Item><Pop style={{ color: 'inherit',textDecoration: 'inherit' }} to="/work">Work</Pop></Item>
                <Item><Pop style={{ color: 'inherit',textDecoration: 'inherit' }} to="/contact">Contact</Pop></Item>
            </ListContainer>
        </Container>
    )
}

export default NavigationBar;
