import React from 'react';
import './Card.css';
import posed, {PoseGroup} from 'react-pose';

const Item = posed.div({
    enter: { opacity: 1, y: 0 },
    exit: { opacity: 0, y: 30, transition: { type: "spring" } }
  });

const Card = ({children}) =>{
    return(
        <Item className="outerCard">
            {children}
        </Item>
    )
}

export default Card;
