import React from 'react';
import './Card.css';
import posed, {PoseGroup} from 'react-pose';

const pop = posed.div({
    enter: { opacity: 1 },
    exit: { opacity: 0 }
});

const Card = ({children}) =>{
    return(
        <pop className="outerCard">
            {children}
        </pop>
    )
}

export default Card;
